import React from 'react';
import axios from 'axios';

interface Props {
  pokemonOne: any;
  pokemonTwo: any;
  setBattleLog: React.Dispatch<React.SetStateAction<string>>;
}

const BattleView: React.FC<Props> = ({ pokemonOne, pokemonTwo, setBattleLog }) => {

  const startBattle = async () => {
    try {
      // Randomly select a move for each Pokémon
      const moveOne = pokemonOne.moves[Math.floor(Math.random() * pokemonOne.moves.length)].move;
      const moveTwo = pokemonTwo.moves[Math.floor(Math.random() * pokemonTwo.moves.length)].move;

      // Fetch move details from the API
      const [moveOneDetails, moveTwoDetails] = await Promise.all([
        axios.get(moveOne.url),
        axios.get(moveTwo.url)
      ]);

      // Extract move power (default to 0 if not available)
      const movePowerOne = moveOneDetails.data.power || 0;
      const movePowerTwo = moveTwoDetails.data.power || 0;

      // Determine the winner based on move power
      if (movePowerOne > movePowerTwo) {
        setBattleLog(`${pokemonOne.name} lands a decisive blow with ${moveOneDetails.data.name}, knocking out ${pokemonTwo.name}!`);
      } else if (movePowerOne < movePowerTwo) {
        setBattleLog(`${pokemonTwo.name} lands a decisive blow with ${moveTwoDetails.data.name}, knocking out ${pokemonOne.name}!`);
      } else {
        setBattleLog(`Draw! Both ${pokemonOne.name} and ${pokemonTwo.name} used equally powerful moves.`);
      }
    } catch (error) {
      console.error('Error fetching move details:', error);
      setBattleLog('An error occurred during the battle.');
    }
  };

  return (
    <div>
      <h2>Battle View</h2>
      <div>
        <h3>{pokemonOne.name}</h3>
        <img src={pokemonOne.sprites.front_default} alt={pokemonOne.name} />
      </div>
      <div>
        <h3>{pokemonTwo.name}</h3>
        <img src={pokemonTwo.sprites.front_default} alt={pokemonTwo.name} />
      </div>
      <button onClick={startBattle}>Start Battle</button>
    </div>
  );
}

export default BattleView;
