import React, { useState } from 'react';
import './App.css';
import PokemonSelector from './components/PokemonSelector';
import BattleView from './components/BattleView';
import BattleLog from './components/BattleLog';

function App() {
  const [pokemonOne, setPokemonOne] = useState<any>(null);
  const [pokemonTwo, setPokemonTwo] = useState<any>(null);
  const [battleLog, setBattleLog] = useState<string[]>([]); // Array to hold the battle log entries

  const resetBattle = () => {
    setPokemonOne(null);
    setPokemonTwo(null);
    // Do not clear the battle log to keep the history of all battles
  };

  const refreshApp = () => {
    window.location.reload(); // Simple refresh of the entire app
  };

  return (
    <div className="App">
      <h1>Pok√©mon Battle App</h1>
      <button onClick={refreshApp}>Refresh App</button>
      {!pokemonOne && !pokemonTwo && (
        <PokemonSelector 
          setPokemonOne={setPokemonOne} 
          setPokemonTwo={setPokemonTwo} 
        />
      )}
      {pokemonOne && pokemonTwo && (
        <BattleView 
          pokemonOne={pokemonOne} 
          pokemonTwo={pokemonTwo} 
          battleLog={battleLog}
          setBattleLog={setBattleLog}
          resetBattle={resetBattle}
        />
      )}
      {battleLog.length > 0 && <BattleLog battleLog={battleLog} />}
    </div>
  );
}

export default App;
